
create table if not exists public.pedagogical_content (
    id bigint generated by default as identity primary key,
    niveau smallint not null,
    dimension character varying not null,
    question text not null,
    objectif text not null,
    tip text,
    tags_theme character varying[] not null,
    tags_filtre character varying[] not null,
    icon_tag character varying
);

grant delete, insert, select, update on table public.pedagogical_content to "service_role";

