INSERT INTO public.pedagogical_content (niveau, dimension, question, objectif, tip, tags_theme, tags_filtre, icon_tag) VALUES
(1, 'COMPRENDRE', 'Comment la marée peut-elle être une aide pour la navigation ?', 'Comprendre le phénomène de la marée et savoir utiliser un annuaire de marée', 'La marée est un courant qui peut nous aider ou nous ralentir. Il faut regarder les horaires et hauteurs pour savoir si elle monte ou descend.', '{"reperes_spatio_temporels"}', '{"marée", "outils"}', 'marée'),
(1, 'OBSERVER', 'Comment identifier les zones de rochers sous l''eau ?', 'Savoir repérer les zones de danger potentiel par l''observation de la surface', 'Des vagues qui déferlent au large, une couleur d''eau différente peuvent indiquer la présence de rochers.', '{"lecture_paysage"}', '{"zone rocheuse", "sécurité"}', 'sécurité'),
(1, 'PROTÉGER', 'Pourquoi ne faut-il pas jeter ses déchets dans la mer ?', 'Comprendre l''impact des déchets sur la faune et la flore marine', 'Même un déchet biodégradable peut mettre beaucoup de temps à disparaître et peut être ingéré par des animaux.', '{"impact_presence_humaine"}', '{"pollution", "éco-geste"}', 'pollution'),
(2, 'COMPRENDRE', 'Qu''est-ce que le chenal et comment le respecter ?', 'Savoir identifier et respecter les zones de navigation pour la sécurité de tous', 'Le chenal est une "route" balisée pour les bateaux. On doit y naviguer avec prudence et respecter les règles de priorité.', '{"activites_humaines"}', '{"chenal", "sécurité"}', 'sécurité'),
(2, 'OBSERVER', 'Qu''est-ce que l''estran et qu''y trouve-t-on ?', 'Découvrir la richesse de la vie sur l''estran (coquillages, algues, petits crustacés)', 'L''estran est la zone découverte par la marée basse. C''est un écosystème très riche mais fragile.', '{"caracteristiques_littoral", "biodiversite_saisonnalite"}', '{"estran", "faune", "flore"}', 'faune'),
(2, 'PROTÉGER', 'Pourquoi certaines zones sont-elles interdites à la navigation ?', 'Identifier et respecter les zones de protection (nidification, culture marine...)', 'Ces zones protègent des espèces sensibles ou des activités économiques. Leur quiétude est essentielle.', '{"cohabitation_vivant", "activites_humaines"}', '{"zone sensible", "reproduction"}', 'sécurité'),
(3, 'COMPRENDRE', 'Comment anticiper l''effet du courant de marée sur sa trajectoire ?', 'Intégrer le courant dans sa planification de navigation pour optimiser sa route et sa sécurité', 'Un courant traversier déporte le bateau. Il faut viser un point au vent du courant pour compenser cet effet.', '{"reperes_spatio_temporels"}', '{"courant", "marée", "adaptation"}', 'marée'),
(3, 'OBSERVER', 'Comment le vent et la marée interagissent-ils ?', 'Observer comment le courant de marée peut modifier l''état de la mer (clapot, vagues plus creuses)', 'Un vent contre un courant fort peut lever une mer très désagréable et dangereuse.', '{"interactions_climatiques"}', '{"vent", "courant", "vague"}', 'vent'),
(3, 'PROTÉGER', 'Comment participer à la surveillance de la qualité de l''eau ?', 'Connaître les actions de sciences participatives et savoir signaler une pollution', 'Des applications ou des numéros existent pour signaler des pollutions ou des phénomènes inhabituels (ex: OBSenMER).', '{"sciences_participatives"}', '{"pollution", "action citoyenne"}', 'pollution'),
(1, 'COMPRENDRE', 'Quels sont les principaux vents sur notre spot de navigation ?', 'Identifier les directions de vent principales et leurs caractéristiques (force, stabilité)', 'Par exemple, le vent d''Ouest est souvent associé à la pluie, tandis que le vent d''Est est plus sec.', '{"interactions_climatiques"}', '{"vent", "météo"}', 'vent'),
(1, 'OBSERVER', 'Comment savoir si la marée monte ou descend en regardant la mer ?', 'Utiliser les repères visuels sur la côte pour déterminer le sens de la marée', 'Regarder les rochers, les algues sur les quais. Sont-ils secs et l''eau est loin, ou sont-ils mouillés et l''eau est proche ?', '{"reperes_spatio_temporels"}', '{"marée", "observation"}', 'marée'),
(1, 'PROTÉGER', 'Quel est l''animal le plus commun que l''on peut observer ici ?', 'Apprendre à reconnaître une espèce locale emblématique et son habitat', 'Exemple: Le goéland argenté. On le trouve partout, il est opportuniste et s''adapte bien à l''homme.', '{"biodiversite_saisonnalite"}', '{"faune", "observation"}', 'faune'),
(2, 'COMPRENDRE', 'C''est quoi le coefficient de marée ?', 'Comprendre que le coefficient indique l''amplitude de la marée (le marnage)', 'Un grand coefficient (proche de 120) signifie une grande différence de hauteur d''eau et des courants forts.', '{"reperes_spatio_temporels"}', '{"marée", "coefficient"}', 'marée'),
(2, 'OBSERVER', 'Comment le vent change-t-il au cours de la journée ?', 'Découvrir le phénomène de la brise thermique et savoir l''anticiper', 'Le soleil chauffe la terre plus vite que la mer. L''air chaud monte, l''air frais de la mer prend sa place: c''est la brise !', '{"interactions_climatiques"}', '{"vent", "thermique"}', 'vent'),
(2, 'PROTÉGER', 'Qu''est-ce que la laisse de mer et pourquoi est-elle importante ?', 'Comprendre que la laisse de mer est un habitat pour de nombreuses petites bêtes et protège la dune', 'La laisse de mer (algues, bois flotté...) est une zone riche qui nourrit beaucoup d''espèces et aide à fixer le sable.', '{"caracteristiques_littoral", "impact_presence_humaine"}', '{"laisse de mer", "écosystème"}', 'flore'),
(3, 'COMPRENDRE', 'Comment utiliser un repère de transit pour se positionner ?', 'Savoir utiliser deux amers alignés pour suivre une route sûre ou connaître sa position', 'Quand le mât du port est aligné avec le clocher de l''église, je suis sur la ligne d''entrée du port.', '{"lecture_paysage", "reperes_spatio_temporels"}', '{"repères visuels", "outils"}', 'repères visuels'),
(3, 'OBSERVER', 'Comment les nuages nous informent-ils sur la météo à venir ?', 'Apprendre à reconnaître quelques types de nuages et la météo qu''ils annoncent', 'Les Cumulonimbus (gros et sombres) annoncent des grains. Les Cirrus (filaments blancs) annoncent souvent un changement de temps.', '{"interactions_climatiques"}', '{"météo", "nuage"}', 'météo'),
(3, 'PROTÉGER', 'Quel est l''impact du bruit de nos bateaux sur la faune marine ?', 'Prendre conscience de la pollution sonore et de ses effets sur les animaux marins', 'Le bruit des moteurs peut perturber la communication, la chasse et la reproduction des mammifères marins.', '{"impact_presence_humaine", "cohabitation_vivant"}', '{"pollution", "faune"}', 'faune'),
(1, 'COMPRENDRE', 'Pourquoi la crème solaire est-elle importante en mer ?', 'Comprendre les risques liés au soleil et à la réverbération sur l''eau', 'Sur l''eau, le soleil nous atteint directement et indirectement par sa réflexion sur la surface. On est doublement exposé !', '{"interactions_climatiques"}', '{"sécurité", "éco-geste"}', 'sécurité'),
(1, 'OBSERVER', 'Quels sont les différents types de bateaux que l''on voit aujourd''hui ?', 'Apprendre à distinguer les voiliers, les bateaux à moteur, les bateaux de pêche...', 'Chaque bateau a une fonction et des règles de priorité différentes.', '{"activites_humaines"}', '{"vocabulaire"}', null),
(1, 'PROTÉGER', 'Que faire si je trouve un animal blessé ?', 'Connaître les bons réflexes : ne pas toucher et appeler un organisme spécialisé', 'Contacter l''observatoire Pelagis ou un centre de soin local. Ne jamais tenter de soigner l''animal soi-même.', '{"cohabitation_vivant"}', '{"sécurité", "faune"}', 'faune'),
(2, 'COMPRENDRE', 'Comment s''habiller pour une sortie en mer ?', 'Savoir adapter sa tenue aux conditions météo et à la température de l''eau', 'Le principe des 3 couches est efficace : une couche respirante, une couche chaude (polaire) et une couche imperméable.', '{"interactions_climatiques"}', '{"sécurité", "adaptation"}', 'sécurité'),
(2, 'OBSERVER', 'Où se trouvent les zones les plus profondes / les moins profondes de notre zone de navigation ?', 'Savoir lire une carte marine simple ou utiliser des repères pour identifier les hauts-fonds', 'Les couleurs sur une carte (bleu foncé/clair), ou l''absence de vagues peuvent indiquer des zones plus profondes.', '{"lecture_paysage", "reperes_spatio_temporels"}', '{"outils", "sécurité"}', 'repères visuels'),
(2, 'PROTÉGER', 'Pourquoi est-il important de ne pas nourrir les animaux sauvages ?', 'Comprendre que nourrir les animaux modifie leur comportement naturel et peut les rendre dépendants ou malades', 'Les animaux perdent leur capacité à se nourrir seuls et peuvent devenir agressifs ou trop familiers avec l''homme.', '{"cohabitation_vivant"}', '{"faune", "éco-geste"}', 'faune'),
(3, 'COMPRENDRE', 'Quelles sont les activités économiques locales liées à la mer ?', 'Découvrir comment la mer est une ressource pour l''économie locale (pêche, tourisme, aquaculture...)', 'Ces activités façonnent le paysage et créent des interactions avec notre pratique.', '{"activites_humaines"}', '{"économie"}', null),
(3, 'OBSERVER', 'Comment repérer la direction du courant ?', 'Utiliser les bouées, les casiers ou les bateaux au mouillage pour visualiser la direction et la force du courant', 'Un bateau au mouillage se met naturellement face au courant (ou au vent si celui-ci est plus fort).', '{"reperes_spatio_temporels"}', '{"courant", "observation"}', 'courant'),
(3, 'PROTÉGER', 'Quels sont les gestes simples pour limiter mon impact écologique lors de ma pratique ?', 'Connaître et appliquer les "éco-gestes du marin"', 'Utiliser une gourde, vérifier son moteur pour éviter les fuites, utiliser des produits d''entretien écologiques...', '{"impact_presence_humaine"}', '{"éco-geste", "action citoyenne"}', 'pollution'),
(1,'COMPRENDRE','Qu''est-ce qu''un herbier de posidonie et pourquoi est-il protégé ?','Comprendre que c''est une plante marine essentielle qui produit de l''oxygène, sert de nurserie et protège les plages.','L''ancre et la chaîne de votre bateau peuvent l''arracher. Il faut privilégier les zones sableuses pour le mouillage.','{"caracteristiques_littoral", "cohabitation_vivant"}','{"flore", "écosystème", "zone sensible"}', 'flore'),
(1,'OBSERVER','A quoi servent les bouées que l''on voit en mer ?','Apprendre à reconnaître les bouées de navigation (cardinales, latérales) et leur signification.','Les couleurs et les formes des bouées indiquent les dangers ou le chemin à suivre. C''est le "code de la route" de la mer.','{"lecture_paysage", "activites_humaines"}','{"repères visuels", "sécurité", "vocabulaire"}', 'repères visuels'),
(1,'PROTÉGER','Où jeter les déchets spécifiques au bateau (huiles, batteries) ?','Savoir qu''il existe des points de collecte spécifiques dans les ports pour les déchets dangereux.','Ne jamais jeter ces déchets dans les poubelles classiques ou dans l''eau, ils sont très toxiques.','{"impact_presence_humaine"}','{"pollution", "éco-geste"}', 'pollution'),
(2,'COMPRENDRE','Qu''est-ce que l''érosion et comment est-elle visible sur notre côte ?','Comprendre que l''érosion est l''usure de la côte par la mer et le vent et savoir en repérer les signes.','Falaises qui reculent, dunes qui s''effritent, blockhaus sur la plage... sont des signes de l''érosion.','{"caracteristiques_littoral"}','{"érosion", "lecture_paysage"}', null),
(2,'OBSERVER','Comment identifier une zone de courant ?','Observer les remous, les "rideaux" à la surface de l''eau ou le sillage des bouées.','Le courant laisse des traces visibles à la surface. Apprendre à les lire, c''est anticiper ses effets.','{"reperes_spatio_temporels"}','{"courant", "observation", "vague"}', 'marée'),
(2,'PROTÉGER','Comment éviter de déranger les oiseaux qui se reposent sur la plage ?','Garder ses distances, ne pas courir vers eux et tenir son chien en laisse.','Les oiseaux ont besoin de se reposer pour reprendre des forces, notamment pendant les migrations. Le dérangement les épuise.','{"cohabitation_vivant"}','{"faune", "zone sensible", "migration"}', 'faune'),
(3,'COMPRENDRE','Qu''est-ce qu''une espèce invasive et pourquoi est-ce un problème ?','Comprendre qu''une espèce invasive est une espèce non-native qui menace la biodiversité locale.','Exemple : la crépidule. Elle se reproduit vite et concurrence les espèces locales pour la nourriture et l''espace.','{"biodiversite_saisonnalite", "impact_presence_humaine"}','{"faune", "flore", "écosystème"}', 'faune'),
(3,'OBSERVER','Comment la forme des vagues change-t-elle à l''approche de la côte ?','Comprendre l''effet des fonds marins sur la houle (hauts-fonds, baïnes).','Quand la profondeur diminue, la vague ralentit, se redresse et finit par déferler. La forme de la vague nous renseigne sur la nature du fond.','{"interactions_climatiques"}','{"vague", "houle", "observation"}', 'marée'),
(3,'PROTÉGER','Quelles sont les innovations qui permettent une pratique nautique plus durable ?','Découvrir les solutions existantes : moteurs électriques, antifouling non toxique, matériaux recyclés...','La technologie peut aider à réduire notre impact, il est bon de se tenir informé des nouveautés.','{"impact_presence_humaine", "activites_humaines"}','{"innovation", "éco-geste"}', null),
(1, 'OBSERVER', 'Comment lire le paysage pour trouver la meilleure zone de mise à l''eau ?', 'Repérer les zones abritées du vent et des vagues, les accès faciles (cales, plages de sable dur).', 'Une bonne observation avant de partir évite les efforts inutiles et les risques.', '{"lecture_paysage"}', '{"sécurité", "adaptation"}', 'sécurité'),
(2, 'COMPRENDRE', 'Pourquoi la dune est-elle un milieu important mais fragile ?', 'Comprendre que la dune protège l''intérieur des terres de la mer et du vent, et qu''elle abrite une flore spécifique.', 'Le piétinement abîme les plantes qui retiennent le sable. Il faut utiliser les chemins balisés.', '{"caracteristiques_littoral"}', '{"dune", "écosystème", "flore"}', 'flore'),
(3, 'PROTÉGER', 'Comment signaler la présence d''un cétacé sans le déranger ?', 'Connaître les bons gestes : ralentir, ne pas couper sa route, et contacter les réseaux de sciences participatives.', 'Signaler une observation de dauphin ou de baleine aide les scientifiques à mieux les connaître et les protéger.', '{"cohabitation_vivant", "sciences_participatives"}', '{"faune", "observation"}', 'faune'),
(1, 'COMPRENDRE', 'Qu''est-ce que ça veut dire "bâbord" et "tribord" ?', 'Apprendre ce vocabulaire de base pour mieux communiquer à bord.', '"Bâbord" est à gauche en regardant l''avant, "tribord" est à droite. Un moyen mnémotechnique : "batterie" (Bâbord-Tribord).', '{"reperes_spatio_temporels"}', '{"vocabulaire"}', null),
(2, 'OBSERVER', 'Comment estimer une distance en mer ?', 'Utiliser des techniques simples : la taille des autres bateaux, le temps pour parcourir une distance avec une vitesse connue...', 'L''estimation des distances est plus difficile en mer car il y a peu de repères. Il faut de l''entraînement.', '{"lecture_paysage"}', '{"observation", "outils"}', 'repères visuels'),
(3, 'COMPRENDRE', 'Quel est le cycle de vie d''un déchet plastique en mer ?', 'Comprendre que le plastique se fragmente en micro-plastiques mais ne disparaît jamais vraiment, polluant durablement.', 'Un sac plastique mettra des centaines d''années à se décomposer en petits morceaux qui seront mangés par les animaux marins.', '{"impact_presence_humaine"}', '{"pollution", "écosystème"}', 'pollution'),
(1, 'COMPRENDRE', 'Quelle est la différence entre la houle et les vagues créées par le vent ?', 'La houle est une ondulation régulière qui a voyagé, le clapot est une vague courte et désordonnée formée par le vent local.', 'La houle peut être présente même sans vent. Le clapot s''arrête peu après le vent.', '{"interactions_climatiques"}', '{"vague", "houle", "vent"}', 'vent'),
(2, 'PROTÉGER', 'Qu''est-ce qu''une zone de mouillage écologique ?', 'Savoir que ce sont des zones équipées de bouées pour s''amarrer sans jeter l''ancre et abîmer les fonds marins.', 'Elles sont souvent installées pour protéger des écosystèmes fragiles comme les herbiers de posidonie.', '{"impact_presence_humaine", "cohabitation_vivant"}', '{"zone sensible", "innovation"}', 'sécurité'),
(3, 'OBSERVER', 'Comment repérer une "zone de dévente" sous le vent d''un obstacle ?', 'Observer la surface de l''eau qui devient lisse et vitreuse derrière une pointe rocheuse ou un grand bâtiment.', 'Une zone de dévente est une zone où le vent est beaucoup plus faible, voire nul. Utile pour s''abriter, mais piège si on ne veut pas s''arrêter !', '{"interactions_climatiques"}', '{"vent", "observation"}', 'vent'),
(1, 'COMPRENDRE', 'Quels sont les numéros d''urgence à connaître en mer ?', 'Mémoriser le 196 (CROSS) ou le canal 16 de la VHF pour toute urgence en mer.', 'Le 196 est le numéro gratuit pour joindre les secours en mer depuis un téléphone. C''est le "18" de la mer.', '{"reperes_spatio_temporels"}', '{"sécurité"}', 'sécurité'),
(2, 'PROTÉGER', 'Pourquoi faut-il nettoyer son matériel après une sortie en mer ?', 'Pour enlever le sel qui abîme le matériel, mais aussi pour éviter de transporter des espèces (algues, coquillages) d''un site à un autre.', 'C''est un geste simple pour l''entretien et pour lutter contre la propagation des espèces invasives.', '{"impact_presence_humaine"}', '{"éco-geste", "outils"}', null),
(3, 'COMPRENDRE', 'Comment la lune influence-t-elle les marées ?', 'Comprendre que l''attraction de la lune "tire" sur les masses d''eau, créant les marées.', 'Le soleil a aussi une influence, mais plus faible. Quand la lune et le soleil sont alignés (pleine/nouvelle lune), les marées sont plus fortes (vives-eaux).', '{"reperes_spatio_temporels"}', '{"marée"}', 'marée');
